mapper.delete:
DELETE FROM CONTENTSLIKE
WHERE USERNUM= #{userNum}
AND CONTENTID = #{contentId}
AND KIND = #{kind}

mapper.select:
SELECT CONTENTID
FROM CONTENTSLIKE
WHERE USERNUM = #{userNum}
AND KIND = #{kind}

SELECT COUNT(*) 
FROM CONTENTSLIKE 
WHERE USERNUM = #{userNum} AND CONTENTID = #{contentId} AND KIND = #{kind}
