mapper.select:
SELECT BOOKID
FROM (
SELECT BOOKID
FROM MOVIEBOOKS
WHERE USERNUM = #{userNum}
AND THEATERID = #{theaterId}
AND NOWSTATUS = 0
ORDER BY BOOKDATE DESC
)
WHERE ROWNUM = 1

SELECT BOOKID
FROM (
SELECT BOOKID
FROM MOVIEBOOKS
WHERE USERNUM = #{userNum}
AND THEATERID = #{theaterId}
AND NOWSTATUS = 2
ORDER BY BOOKDATE DESC
)
WHERE ROWNUM = 1
