mapper.select:
SELECT COUNT(*) 
FROM REVIEWLIKE 
WHERE USERNUM = #{userNum,  jdbcType=NUMERIC} 
AND REVIEWID = #{reviewId,  jdbcType=NUMERIC} 
AND KIND = #{kind, jdbcType=NUMERIC}

mapper.delete:
DELETE FROM REVIEWLIKE
WHERE USERNUM= #{userNum,  jdbcType=NUMERIC}
AND REVIEWID = #{reviewId,  jdbcType=NUMERIC}
AND KIND = #{kind,  jdbcType=NUMERIC}

mapper.select:
SELECT REVIEWID
FROM REVIEWLIKE
WHERE USERNUM = #{userNum,  jdbcType=NUMERIC}
AND KIND = #{kind,  jdbcType=NUMERIC}
