mapper.select:
SELECT T.*, M.MOVIETITLE
FROM THEATER T
JOIN
MOVIES M
ON T.MOVIEID = M.MOVIEID
WHERE TRUNC(THEATERSTART) = #{theaterStart}

	AND THEATERNAME = #{theaterName}


	AND T.MOVIEID = #{movieId}

SELECT COUNT(*)
FROM THEATER
WHERE THEATERNAME = #{theaterName}
AND THEATERSTART < #{theaterEnd}
AND THEATEREND > #{theaterStart}
